struct Update {
    int address;
    bool isCheatCode;
    int length;
    int value_added;
    QVector<unsigned char> origins;
    QVector<unsigned char> bytes;

    Update() : address(0), isCheatCode(0), length(0){}
    Update(int address, QVector<unsigned char> origins, QVector<unsigned char> bytes)
        : address(address), isCheatCode(false), length(0), origins(origins), bytes(bytes) {}
    Update(int address, int length) : address(address), isCheatCode(false), length(length), value_added(0) {}
    Update(int address, int length, int vadded)
        : address(address), isCheatCode(false), length(length), value_added(vadded) {}
    Update(QVector<unsigned char> bytes) : isCheatCode(true), bytes(bytes) {}
};

class Patch {
public:
    QMap<int, QVector<Update> > content;
};

